import {connect} from 'react-redux';
import React from 'react'
import {Calendar, momentLocalizer, Views} from 'react-big-calendar'
import moment from 'moment'
import 'react-big-calendar/lib/css/react-big-calendar.css'
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
	container: {
		display: 'flex',
		flexWrap: 'wrap',
	},
	formControl: {
		margin: theme.spacing(1),
		minWidth: 120,
	},
}));

const SportConatiner = ({dispatch}) => {
	let input;
	let localizer = momentLocalizer(moment); // or globalizeLocalizer
	let myEventsList = [];
	const classes = useStyles();
	let handleSelect = ({start, end}) => {
		setOpen(true);
	};
	const handleClickOpen = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};

	const [open, setOpen] = React.useState(false);
	return (
		<div>
			<form>
				<textarea>
				</textarea>
			</form>
			<div className='container'>
				<Calendar
					localizer={localizer}
					events={myEventsList}
					startAccessor="start"
					selectable
					defaultView={Views.WEEK}
					endAccessor="end"
					onSelectEvent={event => alert(event.title)}
					defaultDate={new Date()}
					onSelectSlot={handleSelect}
					views={['week', 'month']}/>
			</div>
			<Dialog open={open} onClose={handleClose} aria-labelledby='form-dialog-title'>
				<DialogContent>
					<DialogContentText>
						Log Sport Event
					</DialogContentText>
					<form className={classes.container}>
						<FormControl className={classes.formControl}>
							<InputLabel htmlFor="age-native-simple">Tag</InputLabel>
							<Select
								native
								value={state.age}
								onChange={handleChange('age')}
								input={<Input id="age-native-simple" />}
							>
								<option value="" />
								<option value={10}>Ten</option>
								<option value={20}>Twenty</option>
								<option value={30}>Thirty</option>
							</Select>
							<TextField
								autoFocus
								margin="dense"
								id="name"
								label="Comment"
								type="text"
								fullWidth
							/>
						</FormControl>
					</form>
				</DialogContent>
				<DialogActions>
					<Button onClick={handleClose} color="primary">
						Cancel
					</Button>
					<Button onClick={handleClose} color="primary">
						Subscribe
					</Button>
				</DialogActions>
			</Dialog>
		</div>
	)
};


export default connect()(SportConatiner);
